<?php if( $my_related_post_ids = get_post_meta($post->ID, 'my_related_posts', true)) :
	$related_args = array(
		'posts_per_page' => 5, // сколько постов будет указано в админке, столько и выведется
		'post__in'=> explode(',', $my_related_post_ids), // в качестве значения нужно будет передать массив
		'orderby' => 'post__in' // посты будут сортироваться в том же порядке, в котором они перечислены в админке
	);
	$misha_query = new WP_Query( $related_args );
	if( $misha_query->have_posts() ) :
		echo '<div class="best_content"><div class="best_content_container content_horizontal">';
		while( $misha_query->have_posts() ) : $misha_query->the_post();
			// в данном случае посты выводятся просто в виде ссылок
			if ( in_category('promokod')) {
			echo '<!----><div class="entry_wrapper swiper-wrapper promo_wrapper"><article><a href="' . get_permalink( $misha_query->post->ID ) . '" target="_blank" class="card_image_view"></a><div class="info_block_picbox"><picture><source type="image/webp" srcset="'. get_the_post_thumbnail_url($page->ID, 'medium') .'.webp"><img src="'. get_the_post_thumbnail_url($page->ID, 'medium') .'" alt="'.get_the_title().'" class="article-image" width="660" height="370" loading="lazy"></picture></div><div class="wrapper_item"><a href="' . get_permalink( $misha_query->post->ID ) . '" target="_blank"><h2>Промокоды '. get_post_meta($post->ID, 'promoname', true) .' '. date_i18n('F Y') .'</h2></a></div></article></div><!---->';
			} else {
			echo '<!----><div class="entry_wrapper swiper-wrapper"><article><a href="' . get_permalink( $misha_query->post->ID ) . '" target="_blank" class="card_image_view"></a><div class="info_block_picbox"><picture><source type="image/webp" srcset="'. get_the_post_thumbnail_url($page->ID, 'medium') .'.webp"><img src="'. get_the_post_thumbnail_url($page->ID, 'medium') .'" alt="'.get_the_title().'" class="article-image" width="660" height="370" loading="lazy"></picture></div><div class="wrapper_item"><a href="' . get_permalink( $misha_query->post->ID ) . '" target="_blank"><h2>'. get_the_title() .'</h2></a><div class="entry_footer item_color"><div class="content-footer__left"></div></div></div></article></div><!---->';	
			}
		endwhile;
		echo '</div></div>';
	endif;
 
	// не забудьте про эту функцию, её отсутствие может повлиять на другие циклы на странице
	wp_reset_postdata();
endif;?>
	
